// src/UserPage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const UserPage = () => {
    const { userId } = useParams(); // Get userId from the URL
    const [user, setUser] = useState(null);
    const [images, setImages] = useState([]);

    useEffect(() => {
        fetchUser();
        fetchUserImages();
    }, [userId]);

    const fetchUser = async () => {
        try {
            const response = await axios.get(`http://localhost:5000/users/${userId}`);
            setUser(response.data);
        } catch (error) {
            console.error('Error fetching user:', error);
        }
    };

    const fetchUserImages = async () => {
        try {
            const response = await axios.get(`http://localhost:5000/images?userId=${userId}`);
            setImages(response.data);
        } catch (error) {
            console.error('Error fetching user images:', error);
        }
    };

    if (!user) return <div>Loading...</div>;

    return (
        <div>
            <h1>{user.username}'s Profile</h1>
            <h2>Uploaded Images:</h2>
            <ul>
                {images.map((imageUrl, index) => (
                    <li key={index}>
                        <img src={imageUrl} alt={`Uploaded ${index}`} style={{ width: '100px' }} />
                    </li>
                ))}
            </ul>
            {/* You can add more functionalities here, like uploading a profile picture or new images */}
        </div>
    );
};

export default UserPage;
