// src/UserPage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import ProfilePicture from './ProfilePicture';
import ImageUpload from './ImageUpload';
import ImageGallery from './ImageGallery';

const UserPage = ({ userId }) => {
  const [user, setUser] = useState({});
  const [images, setImages] = useState([]);

  useEffect(() => {
    // Fetch user details and images when the component mounts
    const fetchUserData = async () => {
      try {
        const userResponse = await axios.get(`http://localhost:5000/users/${userId}`);
        setUser(userResponse.data);
        
        const imagesResponse = await axios.get(`http://localhost:5000/images?userId=${userId}`);
        setImages(imagesResponse.data);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, [userId]);

  return (
    <div>
      <h1>{user.username}'s Page</h1>
      <ProfilePicture userId={userId} setUser={setUser} />
      <ImageUpload userId={userId} setImages={setImages} />
      <ImageGallery images={images} />
    </div>
  );
};

export default UserPage;
