import React, { useEffect, useState } from 'react';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { Box, ImageList, ImageListItem, ImageListItemBar } from '@mui/material';

function TestPage() {
  const { userId } = useParams(); // Get userId from the URL
  const [user, setUser] = useState(null);
  const [images, setImages] = useState([]);
  const [profileUpdateTrigger, setProfileUpdateTrigger] = useState(false);

  useEffect(() => {
    fetchUser();
    fetchUserImages();
  }, [userId, profileUpdateTrigger]);

  const fetchUser = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/users/${userId}`);
      setUser(response.data);
    } catch (error) {
      console.error('Error fetching user:', error);
    }
  };

  const fetchUserImages = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/images/${userId}`);
      const data = response.data;
      setImages(data.result);
    } catch (error) {
      console.error('Error fetching user images:', error);
    }
  };
  //console.log(images);

  const fetchProfileImage = async () => {

  }

  const [imageFile, setImageFile] = useState(null);
  const [profileImageFile, setProfileImageFile] = useState(null);

  const handleImageFileChange = (e) => {
    setImageFile(e.target.files[0]);
  };
  const handleProfileImageFileChange = (e) => {
    setProfileImageFile(e.target.files[0]);
  };

  const handleProfileUpload = async () => {
    const formData = new FormData();
    formData.append('file', profileImageFile);
    try {
      await axios.post(`http://localhost:5000/upload-profile-pic/${userId}`, formData);
      setProfileUpdateTrigger(prev => !prev);
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  };

  const handleImageUpload = async () => {
    const formData = new FormData();
    formData.append('file', imageFile);
    try {
      await axios.post(`http://localhost:5000/upload/${userId}`, formData);
      fetchUserImages();
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  };

  const handleDownload = (blobName) => {
    window.open(`http://localhost:5000/download/${blobName}`, '_blank');
  };

  const handleDelete = async (blobName, imageId) => {
    try {
      await axios.delete(`http://localhost:5000/images/delete/${imageId}/${blobName}`);
      // Remove the deleted image from the images state
      setImages(prevImages => prevImages.filter(image => image.id !== imageId));
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  };

  console.log(images);

  const itemData = [
    {
      img: 'https://images.unsplash.com/photo-1551963831-b3b1ca40c98e',
      title: 'Breakfast',
    },
    {
      img: 'https://images.unsplash.com/photo-1551782450-a2132b4ba21d',
      title: 'Burger',
    },
    {
      img: 'https://images.unsplash.com/photo-1522770179533-24471fcdba45',
      title: 'Camera',
    },
    {
      img: 'https://images.unsplash.com/photo-1444418776041-9c7e33cc5a9c',
      title: 'Coffee',
    },
    {
      img: 'https://images.unsplash.com/photo-1533827432537-70133748f5c8',
      title: 'Hats',
    },
    {
      img: 'https://images.unsplash.com/photo-1558642452-9d2a7deb7f62',
      title: 'Honey',
    },
    {
      img: 'https://images.unsplash.com/photo-1516802273409-68526ee1bdd6',
      title: 'Basketball',
    },
    {
      img: 'https://images.unsplash.com/photo-1518756131217-31eb79b20e8f',
      title: 'Fern',
    },
    {
      img: 'https://images.unsplash.com/photo-1597645587822-e99fa5d45d25',
      title: 'Mushrooms',
    },
    {
      img: 'https://images.unsplash.com/photo-1567306301408-9b74779a11af',
      title: 'Tomato basil',
    },
    {
      img: 'https://images.unsplash.com/photo-1471357674240-e1a485acb3e1',
      title: 'Sea star',
    },
    {
      img: 'https://images.unsplash.com/photo-1589118949245-7d38baf380d6',
      title: 'Bike',
    },
  ];

  return (
    <div>
      <Box spacing={2} sx={{ p: 2, width: { xs: '100%', md: '100%', lg: '80%' }, margin: 'auto', textAlign: 'center' }}>

        {/* First Stack for Profile and Name */}
        <Stack direction="row" spacing={2} sx={{ mb: 2, alignItems: 'center' }}>
          {/* Profile Image */}
          <div style={{ width: 50, height: 50, borderRadius: '50%', overflow: 'hidden' }}>
            <img
              src="https://vehiclemaintenance.blob.core.windows.net/profilepics/blue-car.png"
              alt="Profile"
              style={{ width: '100%', height: '100%', objectFit: 'cover' }}
            />
          </div>

          {/* Name */}
          <div style={{ textAlign: 'left' }}>
            <strong>John Doe</strong>
          </div>
        </Stack>

        {/* Second Stack for Buttons */}
        <div>
          <ImageList sx={{ width: 500, height: 450 }} cols={3} rowHeight={164}>
          {itemData.map((item) => (
        <ImageListItem key={item.img}>
          <img
            srcSet={`${item.img}?w=164&h=164&fit=crop&auto=format&dpr=2 2x`}
            src={`${item.img}?w=164&h=164&fit=crop&auto=format`}
            alt={item.title}
            loading="lazy"
          />
        </ImageListItem>
      ))}
          </ImageList>
        </div>
      </Box>
    </div>
  );
}

export default TestPage;
