import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const UserPage = () => {
  const { userId } = useParams(); // Get userId from URL
  const [user, setUser] = useState(null);
  const [images, setImages] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/users/${userId}`);
        setUser(response.data); // Set user data
      } catch (error) {
        console.error('Error fetching user:', error);
      }
    };

    const fetchUserImages = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/images?userId=${userId}`);
        setImages(response.data); // Set images data
      } catch (error) {
        console.error('Error fetching user images:', error);
      }
    };

    fetchUser();
    fetchUserImages();
  }, [userId]);

  // Handle file selection
  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  // Handle profile picture upload
  const handleUploadProfilePic = async () => {
    if (!selectedFile) return;

    const formData = new FormData();
    formData.append('file', selectedFile); // Append the file to the form data

    try {
      const response = await axios.post(`http://localhost:5000/upload-profile-pic/${userId}`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setUser((prev) => ({ ...prev, profile_pic: response.data.imageUrl })); // Update user's profile picture
      alert('Profile picture uploaded successfully');
    } catch (error) {
      console.error('Error uploading profile picture:', error);
      alert('Error uploading profile picture');
    }
  };

  return (
    <div>
      {user ? (
        <div>
          <h1>{user.username}'s Page</h1>
          <img src={user.profile_pic} alt={`${user.username}'s profile`} width="150" height="150" />
          <h2>Uploaded Images:</h2>
          <div>
            {images.map((image, index) => (
              <img key={index} src={image.url} alt={`Image ${index}`} width="100" height="100" />
            ))}
          </div>
          
          {/* Profile Picture Upload Form */}
          <div>
            <h2>Set Profile Picture</h2>
            <input type="file" onChange={handleFileChange} />
            <button onClick={handleUploadProfilePic}>Upload Profile Picture</button>
          </div>
        </div>
      ) : (
        <p>Loading user data...</p>
      )}
    </div>
  );
};

export default UserPage;
